mapper.update:
update products
set product_id=#{id}
and product_name = #{name}
and product_stock = #{stock}
and product_quantity = #{quantity}
and product_sales = #{sales}
and product_price = #{price}
and product_kind = #{kind}
and product_status = #{status}
and create_time = #{createTime}
and update_time = #{updateTime}
and shlf_time = #{shlfTime}
and takedown_time = #{takedownTime}
where product_id = #{id};
